import { toast } from './store/toast.js';
export function send(slice) {
    toast.set(slice);
}
export function error(title, message = '') {
    send({ variant: 'error', title, message });
}
export function success(title, message = '') {
    send({ variant: 'success', title, message });
}
export function info(title, message = '') {
    send({ variant: 'info', title, message });
}
export function warning(title, message = '') {
    send({ variant: 'warning', title, message });
}
